  }
);

// fetch the SpaceTypes when we enter the App
TAO.addAsyncHandler(
  { t: 'App', a: 'Enter', o: 'Portal' },
  () => new AppCtx('SpaceType', 'Find', 'Portal'),
);

const SpaceContainer = props => (
  {/* Surround our tree with our DataHandler */}
